mapper.select:
SELECT user_name userName, nick_name nickName, user_type userType, tel_phone telPhone, e_mail eMail, id_card idCard FROM USER_INFO_TBL
and user_name = #{userName}
and pass_word=PASSWORD(#{passWord})

SELECT count(*)
FROM user_info_tbl
WHERE user_name = #{userName}

mapper.update:
UPDATE user_info_tbl SET


        , nick_name=#{nickName}


        , pass_word=PASSWORD(#{passWord})


        , user_type=#{userType}


        , tel_phone=#{telPhone}


        , e_mail=#{eMail}


        , id_card=#{idCard}


WHERE user_name=#{userName}
